<?php

/** @see Bll_Cache */
require_once 'Bll/Cache.php';

/**
 * flash Cache
 *
 * @package    Bll/Cache
 * @copyright  Copyright (c) 2008 Community Factory Inc. (http://communityfactory.com)
 * @create     2009/05/22    Huch
 */
class Mbll_Parking_FlashCache
{
    private static $_prefix = 'Bll_Parking_FlashCache';

    /**
     * get cache key
     *
     * @param string $salt
     * @param mixi $params
     * @return string
     */
    private static function getCacheKey($salt, $params = null)
    {
        return Bll_Cache::getCacheKey(self::$_prefix, $salt, $params);
    }

    /**
     * get new parking flash
     * this function has not use cache for the moment
     *
     * @param array $carInfo     $car('location'=>1, 'cav_name'=>'02_bike', 'color'=>'black')
     * @return stream flash
     */
    public static function getNewFlash($uid, $pid, $app_id)
    {
        $xmlString = self::_getFlashXml();
        
        //echo $xmlString;exit;
        //get car info
        require_once 'Mdal/Parking/Puser.php';
        $mdalPuser = new Mdal_Parking_Puser();
        $carInfo = $mdalPuser->getCarInfo($uid,$pid);

        //change car image    png
        foreach ($carInfo as $item) {
            self::_getBaseFlashCarImage($item['location'], $object_id, $imageBase, $alphaBase);
            self::_getNewFlashCarImage($item['car_id'], $item['color'], $object_id, $imageNew, $alphaNew);

            //change flash car image
            $xmlString = str_replace($imageBase, $imageNew, $xmlString);
            $xmlString = str_replace($alphaBase, $alphaNew, $xmlString);
        }

        //change bg image   jpeg
        $bgBase = self::_getBaseFlashBgImage();
        $bgnew = self::_getNewFlashBgImage($pid);
        $xmlString = str_replace($bgBase, $bgnew, $xmlString);

        //change parking data info
        require_once 'Mbll/Parking/Flash.php';
        $mbllParking = new Mbll_Parking_Flash();
        $content = $mbllParking->getFlashData($uid, $pid, $app_id);
        foreach ($content as $key=>$value) {
            $xmlString = str_replace("{%$" . $key . "%}", $value, $xmlString);
        }
        
        //set process param
        $descriptorspec = array(
           0 => array("pipe", "r"),
           1 => array("pipe", "w"),
           2 => array("file", "xml2swf_error.txt", "a")
        );

        $pipes = array();
        //XML to SWF
        // run swfmill and get $process
        $process = proc_open('/usr/local/swfmill/bin/swfmill -e cp932 xml2swf stdin stdout', $descriptorspec, $pipes);

        if (is_resource($process)) {
            // set param $xmlString
            fwrite($pipes[0], $xmlString);
            fclose($pipes[0]);

            // get $swfOutput
            $swfOutput = stream_get_contents($pipes[1]);
            fclose($pipes[1]);
                
            // close $process
            proc_close($process);
        }

        return $swfOutput;
    }

    /**
     * get base flash xml
     *
     * @return string  flash xml
     */
    private static function _getFlashXml()
    {
        $key = self::getCacheKey('getFlashXml0629');

        if (!$xmlString = Bll_Cache::get($key)) {
            $swfData = file_get_contents(FLASH_GAME_ROOT . '/parkingwar.swf');
                        
            // SWF to XML
            // set process param
            $descriptorspec = array(
               0 => array("pipe", "r"),
               1 => array("pipe", "w"),
               2 => array("file", "swf2xml_error.txt", "a")
            );

            $pipes = array();

            // run swfmill and get $process
            $process = proc_open('/usr/local/swfmill/bin/swfmill -e cp932 swf2xml stdin stdout', $descriptorspec, $pipes);

            if (is_resource($process)) {
                // set param $swfData
                fwrite($pipes[0], $swfData);
                fclose($pipes[0]);

                // get xmlString
                $xmlString = stream_get_contents($pipes[1]);
                fclose($pipes[1]);
                
                // close process
                proc_close($process);
            }
            
            if ($xmlString) {
                Bll_Cache::set($key, $xmlString, Bll_Cache::LIFE_TIME_ONE_MONTH);
            }
        }

        return $xmlString;
    }

    /**
     * get new flash car image
     *
     * @param integer $car_id
     * @param string $color
     * @param integer $object_id
     * @param string $imageNew
     * @param string $alphaNew
     */
    private static function _getNewFlashCarImage($car_id, $color, $object_id, &$imageNew, &$alphaNew)
    {
        require_once 'Mdal/Parking/Puser.php';
        $mdalPuser = new Mdal_Parking_Puser();
        $image = $mdalPuser->getFlashCarImage($car_id,$color);

        $alphaNew = '<DefineBitsJPEG3 objectID="' . $object_id . '" offset_to_alpha="' . $image['offset_to_alpha'] . '">';
        $imageNew = $image['data'];
    }

    /**
     * get base flash car image
     *
     * @param integer $id
     * @param integer $object_id
     * @param string $imageBase  base64
     * @param string $alphaBase  base64
     */
    private static function _getBaseFlashCarImage($id, &$object_id, &$imageBase, &$alphaBase)
    {
        switch ($id) {
            case 1 :
                $alphaBase = '<DefineBitsJPEG3 objectID="53" offset_to_alpha="2002">';
				$imageBase = '';
                $object_id = '53';
                break;
            case 2 :
                $alphaBase = '<DefineBitsJPEG3 objectID="55" offset_to_alpha="2055">';
				$imageBase = '';
                $object_id = '55';
                break;
            case 3 :
                $alphaBase = '<DefineBitsJPEG3 objectID="57" offset_to_alpha="2133">';
				$imageBase = '';
                $object_id = '57';
                break;
            case 4 :
                $alphaBase = '<DefineBitsJPEG3 objectID="59" offset_to_alpha="2091">';
				$imageBase = '';
                $object_id = '59';
                break;
            case 5 :
                $alphaBase = '<DefineBitsJPEG3 objectID="61" offset_to_alpha="2233">';
				$imageBase = '';
                $object_id = '61';
                break;
            case 6 :
                $alphaBase = '<DefineBitsJPEG3 objectID="63" offset_to_alpha="2159">';
				$imageBase = '';
                $object_id = '63';
                break;
            case 7 :
                $alphaBase = '<DefineBitsJPEG3 objectID="65" offset_to_alpha="2084">';
				$imageBase = '';
                $object_id = '65';
                break;
            case 8 :
                $alphaBase = '<DefineBitsJPEG3 objectID="67" offset_to_alpha="2294">';
				$imageBase = '';
                $object_id = '67';
                break;
        }
    }

    /**
	 * get base flash bg image
	 *
	 * @return string
	 */
    private static function _getBaseFlashBgImage()
	{
        return '';
	}

    /**
     * get new flash bg image
     *
     * @param string $uid
     * @return string  base64_encode
     */
    private static function _getNewFlashBgImage($uid)
    {
        if ($uid == '-1' || $uid == '-2') {
            $path = Zend_Registry::get('static') . '/apps/parking/mimg/mobileBg/0' . -$uid . '.jpg';
        }
        else {
            require_once 'Dal/Parking/House.php';
            $dalHouse = new Dal_Parking_House();
            $house = $dalHouse->getOldHouseInfo($uid);

            $path = Zend_Registry::get('static') . '/apps/parking/mimg/mobileBg/' . $house['cav_name'] . '.jpg';
        }

        $replaceImageData = file_get_contents($path);
        return base64_encode("\xFF\xD9\xFF\xD8" . $replaceImageData);
    }

    /**
     * clear feed template cache info
     *
     * @param integer $app_id application id
     * @param integer $template_id template id
     */
    public static function clearFlash()
    {
        Bll_Cache::delete(self::getCacheKey('getFlashXml'));
    }
}